import{_ as i,V as l,W as e,a1 as a}from"./framework-5e2051d8.js";const r={},d=a('<h2 id="vim的基本操作" tabindex="-1"><a class="header-anchor" href="#vim的基本操作" aria-hidden="true">#</a> vim的基本操作</h2><ul><li>在普通模式.重复上一次操作</li><li>执行制定次数相同的命令</li></ul><h3 id="游标的快速跳转" tabindex="-1"><a class="header-anchor" href="#游标的快速跳转" aria-hidden="true">#</a> 游标的快速跳转</h3><p>** 行间跳转 😗*</p><ol><li>nG表示跳转到地n行</li><li>gg游标移动到第一行</li><li>G跳转到行尾</li><li>ctrl 回到游标跳转前的位置</li></ol><h3 id="复制粘贴和剪切" tabindex="-1"><a class="header-anchor" href="#复制粘贴和剪切" aria-hidden="true">#</a> 复制粘贴和剪切</h3><ul><li>复制和粘贴文本 <ol><li>yy表示复制整行</li><li>yw：复制一个单词</li><li>y$复制到行尾</li><li>y^复制到行头</li><li>y^复制到行头</li></ol></li></ul><h3 id="字符替换及撤销操作" tabindex="-1"><a class="header-anchor" href="#字符替换及撤销操作" aria-hidden="true">#</a> 字符替换及撤销操作</h3><ol><li>r替换光标所在的字母</li><li>R 连续替换，直到按下esc</li><li>cc替换整行,进入插入模式</li><li>cw替换一个单词，进入插入模式</li><li>u{n}撤销一次或者n次操作</li><li>ctrl+r恢复撤销的操作</li></ol><h3 id="快速缩进" tabindex="-1"><a class="header-anchor" href="#快速缩进" aria-hidden="true">#</a> 快速缩进</h3><ol><li><code>&gt;&gt;</code> 整行向右缩进</li><li><code>&lt;&lt;</code> 整行向左缩进</li><li>命令行模式输入ce是选中的行的内容居中</li></ol><h3 id="查找" tabindex="-1"><a class="header-anchor" href="#查找" aria-hidden="true">#</a> 查找</h3><ol><li>普通模式使用/向下查找</li><li>?向上查找</li><li>n 表示继续查找</li></ol><h3 id="使用vim编辑多个文件" tabindex="-1"><a class="header-anchor" href="#使用vim编辑多个文件" aria-hidden="true">#</a> 使用vim编辑多个文件</h3><ol><li>命令模式输入n编辑下一个文件</li><li>N编辑上一个文件，加! 强制进行切换</li><li>进入vim打开新文件，同时编辑多个文件，在命令模式下输入e：文件名</li><li>e#回到前一个打开的文件</li><li>ls可以列出编辑过的文档</li><li>恢复文件 使用vim -r 打开文件 输入:ewcover 文件名来恢复</li><li>打开新窗口 命令模式下输: <code>new</code></li></ol>',15),h=[d];function c(n,o){return l(),e("div",null,h)}const s=i(r,[["render",c],["__file","Vim基本操作.html.vue"]]);export{s as default};
